        ArrayList&lt;Integer&gt; arr = new ArrayList&lt;Integer&gt;();
        arr.add(2);
        arr.add(3);
    
        int counter = 4;
    
        while(arr.size() &lt; inp) {
            if(counter % 2 != 0 &amp;&amp; counter%3 != 0) {
                int temp = 4;
                while(temp*temp &lt;= counter) {
                    if(counter % temp == 0)
                        break;
                    temp ++;
                }
                if(temp*temp &gt; counter) {
                    arr.add(counter);
                }
            }
            counter++;
        }
    
        System.out.println(&quot;finish&quot; +arr.get(inp-1));
        }
