        	public ABCPrinter(final Object mutex, final boolean printUpperCaseBoolean) {
        		this.mutex = mutex;
        		this.printUpperCaseBoolean = printUpperCaseBoolean;
        	}
        
        
        	@Override
        	public void run() {
        		while (currentChar &lt;= &#39;z&#39;) {
