        					// Wait so that this thread can&#39;t start directly again
        					Thread.sleep(100);
        					synchronized (mutex) {
        						System.out.println(currentChar);
        						currentChar++;
        						mutex.notify();
        					}
        				}
        				catch (InterruptedException e) {
        					e.printStackTrace();
        				}
        			}
