		public static Price ofJsonNode(JsonNode node) {
			double amount = node.get(&quot;amount&quot;).asDouble();
			double baseAmount = node.get(&quot;baseAmount&quot;).asDouble();
			return new Price(amount, baseAmount);
		}

		public double getAmount() {
