    static final int WRITEBUF_SIZE = 4096;
    byte[] rbuf = new byte[READBUF_SIZE];
    byte[] wbuf = new byte[WRITEBUF_SIZE];
    int mReadSize = 0;
    private SSPDevice sspDevice = null;


    public ITLDeviceCom(){

        sspSystem = new SSPSystem();

        sspSystem.setOnDeviceSetupListener(this);
