```java
public List&lt;Book&gt; getBooksByAuthorAge(int age) {
    Set&lt;String&gt; authorIds = authorList
                .stream()
                .filter(author -&gt; author.getAge() == age)
                .map(Author::getId)
                .collect(Collectors.toSet());

    return bookList
