Here&#39;s a solution using a switch expression.

    static Map&lt;String, Object&gt; jsonToMap(Reader reader) {
      var jsonReader = Json.createReader(reader);
      var jsonValue = jsonReader.readValue(); // Removes duplicate keys (last one wins)
      @SuppressWarnings(&quot;unchecked&quot;)
      var map = (Map&lt;String, Object&gt;) jsonValueToObject(jsonValue);
      return map;
    }

    private static Object jsonValueToObject(JsonValue value) {
      return switch (value.getValueType()) {
        case OBJECT -&gt; ((JsonObject) value).entrySet().parallelStream().collect(
            HashMap::new,
            (map, entry) -&gt; map.put(entry.getKey(), jsonValueToObject(entry.getValue())),
            (map1, map2) -&gt; map2.entrySet().forEach(entry -&gt; map1.put(entry.getKey(), entry.getValue())));
        case ARRAY -&gt; ((JsonArray) value).stream().map(Utils::jsonValueToObject).collect(toList());
        case STRING -&gt; ((JsonString) value).getString();
        case NUMBER -&gt; ((JsonNumber) value).numberValue();
        case TRUE -&gt; Boolean.TRUE;
        case FALSE -&gt; Boolean.FALSE;
        default -&gt; null; // Only JsonValue.NULL remains
      };
    }

