            System.out.println(&quot;  2.2. Optional.isPresent() == false&quot;);
            phone = test.findMyPhone(-1).orElseGet(() -&gt; test.buyNewExpensivePhone());
            System.out.println(&quot;\tUsed phone: &quot; + phone + &quot;\n&quot;);
        }
