````
&lt;module name=&quot;Checker&quot;&gt;
  &lt;module name=&quot;TreeWalker&quot;&gt;
        &lt;module name=&quot;MatchXpath&quot;&gt;
            &lt;property name=&quot;query&quot;
                      value=&quot;//VARIABLE_DEF[./ASSIGN/EXPR and not(./TYPE/IDENT[@text=&#39;var&#39;])]&quot;/&gt;
            &lt;message key=&quot;matchxpath.match&quot;
                     value=&quot;Local variables should use &#39;var&#39; keyword&quot;/&gt;
