final class MapUtil {

    public static List&lt;RechargePlanEntity&gt; toEntity(OperatorWiseCircles in) {
        return in.getCircleWisePlanLists()
                .stream()
                .map(MapUtil::map)
                .peek(out -&gt; map(in, out))
                .collect(Collectors.toList());
