		private boolean knowsWithDegreeRecursive(String a, String b, int x, Set&lt;String&gt; visited) {
			if (x &lt; 1) {
				// x must be at least 1
				return false;
			}
			

			if (map.get(a).contains(b)) {
				// If a knows b, then the number of degrees should be 1
				return x == 1;
