    @ManyToOne
    private Sale sale;

    // id attribute mapped by join column default 
    @MapsId(&quot;productId&quot;) // maps productId attribute of embedded id 
    @ManyToOne
    private Product product;

    @Column(name = &quot;quantity_sold&quot;)
    private int quantitySold;
    
