      &lt;artifactId&gt;axis&lt;/artifactId&gt;
      &lt;version&gt;1.4.1-SNAPSHOT&lt;/version&gt;
      &lt;exclusions&gt;
        &lt;exclusion&gt;
          &lt;groupId&gt;xerces&lt;/groupId&gt;
