    public class LiteralMapFactory {
    
        // Creates a map from a list of entries
        @SafeVarargs
        public static &lt;K, V&gt; Map&lt;K, V&gt; mapOf(Map.Entry&lt;K, V&gt;... entries) {
            LinkedHashMap&lt;K, V&gt; map = new LinkedHashMap&lt;&gt;();
            for (Map.Entry&lt;K, V&gt; entry : entries) {
