        // Creates a map entry
        public static &lt;K, V&gt; Map.Entry&lt;K, V&gt; entry(K key, V value) {
            return new AbstractMap.SimpleEntry&lt;&gt;(key, value);
        }
    
        public static void main(String[] args) {
            System.out.println(mapOf(entry(&quot;a&quot;, 1), entry(&quot;b&quot;, 2), entry(&quot;c&quot;, 3)));
