    public static class Factory{
		final static HashMap&lt;String,Class&lt;? extends MyAbstractClass&gt;&gt; supportedVersions = new HashMap&lt;&gt;();
		static{

			supportedVersions.put(&quot;v1&quot;, ClassV1.class);
