@Aspect
@Component
public class AppendQuestionMarkAspect {

    @Around(&quot;@annotation(AppendQuestionMark)&quot;)
    public Object appendQuestionMark(ProceedingJoinPoint joinPoint) throws Throwable {
        Object[] arguments = joinPoint.getArgs();
