    private static void recursiveFolderDelete(ChannelSftp channelSftp, String path, boolean verbose, boolean simulate) throws SftpException {

        // List source directory structure.
        Vector&lt;ChannelSftp.LsEntry&gt; fileAndFolderList = channelSftp.ls(path);

        // Iterate objects in the list to get file/folder names.
        for (ChannelSftp.LsEntry item : fileAndFolderList) {
          switch (item.getFilename()) {
            case &quot;.&quot;:
            case &quot;..&quot;:
              continue;
          }
          if (!path.endsWith(&quot;/&quot;))
              path += &#39;/&#39;;
          String childPath = path + item.getFilename();
          if (item.getAttrs().isDir()) {
            recursiveFolderDelete(channelSftp, childPath, verbose, simulate);
          } else {
            System.out.println(&quot;rm &quot; + childPath);
            if (!simulate) {
