        Stack&lt;Character&gt; ops = new Stack&lt;Character&gt;();
        for (int i = 0; i &lt; tokens.length; i++) {
            //infix
            // Current token is a whitespace, skip it
            if (tokens[i] == &#39; &#39;) {
                continue;
            }
            // Current token is a number, push it to stack for numbers
            else if (tokens[i] &gt;= &#39;0&#39; &amp;&amp; tokens[i] &lt;= &#39;9&#39;) {
                StringBuffer sbuf = new StringBuffer();
                // There may be more than one digits in number

                while (i &lt; tokens.length &amp;&amp; tokens[i] &gt;= &#39;0&#39; &amp;&amp; tokens[i] &lt;= &#39;9&#39;) {
                    sbuf.append(tokens[i]);
                    if ((i+1)&lt;tokens.length &amp;&amp;tokens[i + 1] &gt;= &#39;0&#39; &amp;&amp; tokens[i + 1] &lt;= &#39;9&#39;) {
                        i++;
                    } else {
                        break;
                    }
                }
                values.add(Double.parseDouble(sbuf.toString()));
            } else if (tokens[i] == &#39;*&#39; || tokens[i] == &#39;-&#39; || tokens[i] == &#39;/&#39; || tokens[i] == &#39;+&#39;) {
                if (ops.isEmpty()) {
                    ops.push(tokens[i]);
                    continue;
                }
                char op1 = ops.peek();
                boolean hasHighPrecedence = hasPrecedence(op1, tokens[i]);
                if (hasHighPrecedence) {
                    char op = ops.pop();
                    values.add(op);
                    ops.push(tokens[i]);
                } else {
                    ops.push(tokens[i]);
                }
            } else if (tokens[i] == &#39;(&#39;) {
                ops.push(tokens[i]);
            } else if (tokens[i] == &#39;)&#39;) {
                while (ops.peek() != &#39;(&#39;) {
                    values.add(ops.pop());
                }
                ops.pop();
            }
        }
        while (!ops.isEmpty()) {
            values.add(ops.pop());
        }
        //post fix
        Stack&lt;Double&gt; numStack = new Stack&lt;&gt;();
        while (!values.isEmpty()) {
            Object val = values.poll();
            if (val instanceof Character) {
                char v = (Character) val;
                if (v == &#39;*&#39; || v == &#39;-&#39; || v == &#39;/&#39; || v == &#39;+&#39;) {
                    double num2, num1;
                    num1 = numStack.pop();
                    num2 = numStack.pop();
                    double ans = applyOp(v, num1, num2);
                    numStack.push(ans);

                }
            } else {
                double num = (double) val;
                numStack.push(num);
            }
        }
        return numStack.pop();

    }

    public static double applyOp(char op, double b, double a) {
        switch (op) {
            case &#39;+&#39;:
                return a + b;
            case &#39;-&#39;:
