                while (ops.peek() != &#39;(&#39;)
                  values.push(applyOp(ops.pop(), values.pop(), values.pop()));
                ops.pop();
            }
 
            // Current token is an operator.
            else if (tokens[i] == &#39;+&#39; || tokens[i] == &#39;-&#39; ||
                     tokens[i] == &#39;*&#39; || tokens[i] == &#39;/&#39;)
            {
