    // Returns true if &#39;op2&#39; has higher or same precedence as &#39;op1&#39;,
    // otherwise returns false.
    public static boolean hasPrecedence(char op1, char op2)
    {
        if (op2 == &#39;(&#39; || op2 == &#39;)&#39;)
            return false;
        if ((op1 == &#39;*&#39; || op1 == &#39;/&#39;) &amp;&amp; (op2 == &#39;+&#39; || op2 == &#39;-&#39;))
