		for (int i = 0; i &lt; arrays.length; i++)
			arrays[i] = rotateArray(arrays[i], 8);
		System.out.println(&quot;Rotated:\n&quot; + prettyPrintArray(arrays));
	}

	private static &lt;E&gt; E[] rotateArray(E[] array, int amount) {
		int arrayLength = array.length;
		if (arrayLength &lt; 2 || amount == 0)
			return array;

		int delta = amount % arrayLength;
		E[] temporary = (E[]) Array.newInstance(array[0].getClass(), arrayLength);
		for (int i = 0; i &lt; arrayLength; i++)
			temporary[(i + delta) % arrayLength] = array[i];

		return temporary;
	}

	private static String prettyPrintArray(Object[][] array) {
		StringBuilder builder = new StringBuilder(&quot;[&quot;);

		for (int i = 0; i &lt; array.length; i++) {
			if (i != 0)
				builder.append(&#39;,&#39;);

