import java.awt.Font;

import javax.swing.JFrame;
import javax.swing.JPanel;

public class Game {
    private Board layout;

    //setup
    public static final int WIDTH = 40;
    public static final int HEIGHT = 40;
    public static final int X = 30;
    public static final int Y = 30;
    protected JFrame window;

    //game attributes
    public static final int BOMB_COUNT = 100;
    private int SCORE_BOARD_WIDTH = 100;
    private static boolean end = false;
    private boolean win = false;
    private static int flags;
    private static int realFlags;
    private Listener gameListener;

    public Game(String title) {
        gameListener = new Listener();
        window = new JFrame();
        flags = BOMB_COUNT;
        realFlags = flags;
        window.setTitle(title);
        window.setSize(X * WIDTH, Y * HEIGHT);
        window.setLocation(1280 - (WIDTH * X) / 2, 720 - (HEIGHT * Y) / 2);
        window.setUndecorated(true);
        window.setResizable(true);
        window.addMouseListener(gameListener);
        window.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    
        window.setVisible(true);

        layout = new Board(this);
        layout.draw();
        Start();
    }

    public static BufferedImage resizeImage(BufferedImage originalImage, int width, int height) throws IOException {
        Image resultingImage = originalImage.getScaledInstance(width, height, Image.SCALE_DEFAULT);
        BufferedImage outputImage = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);
        outputImage.getGraphics().drawImage(resultingImage, 0, 0, null);
        return outputImage;
    }

    private void Start() {
        System.out.println(gameListener.toString());        
        while (!end) {
            if (realFlags == 0) {
                win = true;
                end = true;
            }
            layout.draw();
        }
        layout.draw();
        End();
    }

    private void End() {
        gameListener = null;
        JPanel panel = new JPanel();
        panel.setSize( (int) window.getWidth() * 2 / 3, (int) window.getHeight() * 2 / 3);
        panel.setLocation(window.getWidth() / 2 - panel.getWidth() / 2, window.getHeight() / 2 - panel.getHeight() / 2);
        window.add(panel);
        panel.setVisible(true);
        Graphics g = panel.getGraphics();
        g.setColor(Color.GRAY);
        g.fillRect(0, 0, panel.getWidth(), panel.getHeight());
        g.setColor(Color.PINK);
        g.setFont(new Font(&quot;tnr&quot;, Font.ROMAN_BASELINE, 100));
        if (win) {
            g.drawString(&quot;You Won!&quot;, 165, 200);
        } else {
            g.drawString(&quot;You Lost!&quot;, 175, 200);
        }
        g.drawString(&quot;Play Again?&quot;, 150, 400);
        g.setColor(Color.GREEN);
        g.fillRect(50, 500, 700, 100);
        g.setColor(Color.BLACK);
        g.drawString(&quot;Yes&quot;, panel.getWidth() / 2 - 100, 585);
        panel.addMouseListener(new Listener2());
        g.dispose();
    }

    private void Replay() {
        window.removeAll();
        end = false;
        win = false;
        gameListener = new Listener();
        flags = BOMB_COUNT;
        realFlags = flags;

        window.addMouseListener(gameListener);

        window.setVisible(true);
        
        layout = new Board(this);

        Start();
    }

    private void Close() {
        window.dispose();
        System.exit(0);
    }

    public void lose() {
        end = true;
        win = false;
        End();
    }

