        properties.list().forEach((beanName, mailProperties) -&gt; {
            GenericBeanDefinition definition = new GenericBeanDefinition();
            definition.setBeanClass(JavaMailSenderImpl.class);
            definition.setInstanceSupplier(() -&gt; {
