            System.out.println(newString);
            return;
        }
        char currChar = str.charAt(idx);
        if (map[currChar - &#39;a&#39;]) {
            removeDuplicates(str, idx + 1, newString);
        } else {
            newString += currChar;
            map[currChar - &#39;a&#39;] = true;
            removeDuplicates(str, idx + 1, newString);
        }
