              String fileToAddOrOverwrite = filesToAddOrOverwrite[i];
              try (InputStream in = filesToAddOrOverwriteInputStreams[i]) {
                out.putNextEntry(new ZipEntry(fileToAddOrOverwrite));
                IOUtils.copy(in, out);
                out.closeEntry();
              }
            }
          }
    
        }
    
      }

