    public Integer findTheWord(String stringToCheck, String regexString) {
        Pattern regexp = Pattern.compile(&quot;\\b&quot; + regexString + &quot;\\b&quot;);
        Matcher matcher = regexp.matcher(stringToCheck);

        int count = 0;
        while (matcher.find()) {
            count++;
        }
        return count;
