            final String word = &quot; [00:00:00.000] &quot;;
            final Pattern pattern = Pattern.compile(&quot;^\\s\\[[0-9:.]*\\]\\s&quot;);
            final Matcher matcher = pattern.matcher(word);

            assertTrue(matcher.matches());
        }

    }

