    		Matcher matcher = re.matcher(input);
    		
    		ArrayList&lt;String&gt; tokens = new ArrayList&lt;String&gt;();
    		ArrayList&lt;String&gt; curly = new ArrayList&lt;String&gt;();
    		ArrayList&lt;String&gt; pointy = new ArrayList&lt;String&gt;();
    		
