public static int recursiveBinarySearch(int array[], int key, int high, int low) {
    
    int mid = (high + low) / 2;
    int middleNumber = array[mid];
        
    if (key == middleNumber )
        return mid;
    else if (low &gt; high)
        return -1;
        
    else {
        if (middleNumber &gt; key) {
            high = mid - 1; 
            return recursiveBinarySearch(array, key, high, low);
        }
        
