        List&lt;StockItemResponseDto&gt; stockItemResponseDtoList = fetchItemsFromStockServiceByISBNs(reservationBooksIsbns);
        if (reservationBooksIsbns.size() != stockItemResponseDtoList.size()) {
            throw new ProductNotAvailableException(&quot;Not all products from the list are available in stock. Please verify your request and try again.&quot;);
        }
        performCheckIfThereIsEnoughItemsInStock(reservationDto, stockItemResponseDtoList);
        return reservationMapper.map(saveReservationToDatabase(reservationDto));
    }

    private List&lt;StockItemResponseDto&gt; fetchItemsFromStockServiceByISBNs(List&lt;String&gt; isbns) {
        Object[] rawResponse = webClientBuilder.build().get()
                .uri(String.format(&quot;http://apiGateway/api/stock?objectIsbns=%s&quot;, String.join(&quot;,&quot;, isbns)))
                .retrieve()
                .onStatus(HttpStatusCode::isError, clientResponse -&gt; Mono.error(new ExternalServiceErrorException(&quot;Sorry&quot;)))
                .bodyToMono(Object[].class)
                .block();
        return Arrays.stream(rawResponse)
                .map(object -&gt; mapper.convertValue(object, StockItemResponseDto.class))
                .collect(Collectors.toList());
    }

    private Reservation saveReservationToDatabase(ReservationDto reservationDto) {
        Reservation reservation;
        reservationDto.getReservationItemList().forEach(reservationItem -&gt; reservationItem.setTimestampFrom(Instant.now().getEpochSecond()));
        reservationDto.getReservationItemList().forEach(reservationItem -&gt; reservationItem.setTimestampUntil(Instant.now().getEpochSecond() + TimeUnit.DAYS.toSeconds(1) * 30));
        if (reservationRepository.findByUserEmail(reservationDto.getUserEmail()).isEmpty()) {
            reservation = reservationMapper.map(reservationDto);
        } else {
            Optional&lt;Reservation&gt; reservationOptional = reservationRepository.findByUserEmail(reservationDto.getUserEmail());
            Reservation reservationNew = reservationMapper.map(reservationDto);
            reservation = reservationOptional.get();
            reservation.getReservationItemList().addAll(reservationNew.getReservationItemList());
        }

        return reservationRepository.save(reservation);
    }

    private void performCheckIfThereIsEnoughItemsInStock(ReservationDto reservationDto, List&lt;StockItemResponseDto&gt; stockItemResponseDtoList) {
        for (ReservationItem reservationItem : reservationDto.getReservationItemList()) {
            Optional&lt;StockItemResponseDto&gt; currentStockItem = stockItemResponseDtoList.stream()
                    .filter(stockItem -&gt; Objects.equals(stockItem.getIsbn(), reservationItem.getBookIsbn()))
                    .distinct().findFirst();
            if (currentStockItem.isEmpty()) {
                throw new ProductDoesNotExistException(&quot;Product with isbn: &quot; + reservationItem.getBookIsbn() + &quot; does not exist in our stock.&quot;);
            }
            StockItemResponseDto stockItemResponseDto = currentStockItem.get();
            if (stockItemResponseDto.getQuantityInStock() &lt; reservationItem.getQuantity()) {
                throw new ProductNotAvailableException(&quot;There is not enough books in stock. Faulty ISBN:&quot; + stockItemResponseDto.getIsbn());
            }

        }
    }
