	private static &lt;E&gt; E[] rotateArray(E[] array, int amount) {
		int arrayLength = array.length;
		if (arrayLength &lt; 2 || amount == 0)
			return array;

		int delta = amount % arrayLength;
		E[] temporary = (E[]) Array.newInstance(array[0].getClass(), arrayLength);
		for (int i = 0; i &lt; arrayLength; i++)
			temporary[(i + delta) % arrayLength] = array[i];

		return temporary;
	}

