import java.awt.Graphics;
import java.awt.Point;
import java.awt.Color;

import javax.swing.JPanel;

public class Board {

    private Game game;
    private JPanel panel;

    private Space[][] board = new Space[Game.X][Game.Y];
    public Board(Game game) {
        this.game = game;
        panel = new JPanel();
        panel.setSize(Game.X * Game.WIDTH, Game.Y * Game.HEIGHT);
        panel.setLocation(0, 0);
        panel.setOpaque(true);
        game.window.add(panel);
        panel.setVisible(true);
        
        for (int i = 0; i &lt; Game.X; i++) {
            for (int j = 0; j &lt; Game.Y; j++) {
                try {
                board[i][j] = new Space(false);
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
        int rem = Game.BOMB_COUNT;
        while (rem &gt; 0) {
            try {
                int i = (int) Math.floor(Game.X * Math.random()), j = (int) Math.floor(Game.Y * Math.random());
                if (!board[i][j].hasBomb()) {
                    board[i][j] = new Space(true);
                    rem--;
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        } 

        calculate();
    }

    private void calculate() {
        for (int x = 0; x &lt; Game.X; x++) {
            for (int y = 0; y &lt; Game.Y; y++) {
                if (!board[x][y].hasBomb()) {
                    int count = 0;
                if (x &gt; 0 &amp;&amp; board[x - 1][y].hasBomb()) {
                    count++;
                }
                if (x &lt; Game.X - 1 &amp;&amp; board[x + 1][y].hasBomb()) {
                    count ++;
                }   
                if (y &gt; 0 &amp;&amp; board[x][y - 1].hasBomb()) {
                    count++;
                }
                if (y &lt; Game.Y - 1 &amp;&amp; board[x][y + 1].hasBomb()) {
                    count++;
                }
                if (x &gt; 0 &amp;&amp; y &gt; 0 &amp;&amp; board[x - 1][y - 1].hasBomb()) {
                    count++;
                }
                if (x &lt; Game.X - 1 &amp;&amp; y &gt; 0 &amp;&amp; board[x + 1][y - 1].hasBomb()) {
                    count++;
                }
                if (x &gt; 0 &amp;&amp; y &lt; Game.Y - 1 &amp;&amp; board[x - 1][y + 1].hasBomb()) {
                    count++;
                }
                if (x &lt; Game.X - 1 &amp;&amp; y &lt; Game.Y - 1 &amp;&amp; board[x + 1][y + 1].hasBomb()) {
                    count++;
                }
                board[x][y].setBombsNearby(count);
            }
            }
        }
    }

    public void draw() {
        panel.setBackground(Color.BLACK);
        Graphics g = panel.getGraphics();
        for (int i = 0; i &lt; Game.X; i++) {
            for (int j = 0; j &lt; Game.Y; j++) {
                g.drawImage(board[i][j].getImage(), i * Game.WIDTH, j * Game.HEIGHT, null);
            }
        }
        g.dispose();
    }

    public void Click(Point p) {
        int i = (int) p.getX(), j = (int) p.getY();
        if (i &gt;= Game.X || j &gt;= Game.Y || i &lt; 0 || j &lt; 0) return;
        if (board[i][j].isCleared() || board[i][j].isFlagged()) return;
        if (board[i][j].hasBomb()) {
            try{
                board[i][j].clear();
                board[i][j].updateImage();
            } catch (Exception e) {
                e.printStackTrace();
            }
            game.lose();
            return;
        }
            try {
                board[i][j].updateImage();
                board[i][j].clear();
            } catch (Exception e) {
                e.printStackTrace();
            }
        if (board[i][j].getBombsNearby() == 0) {
            Click(new Point(i - 1, j - 1));
            Click(new Point(i - 1, j));
            Click(new Point(i - 1, j + 1));
            Click(new Point(i, j - 1));
