);
```

**Solution 2**: Write custom RequestLoggingFilter

```java
import com.jayway.jsonpath.JsonPath;
import io.restassured.filter.Filter;
import io.restassured.filter.FilterContext;
import io.restassured.internal.support.Prettifier;
import io.restassured.parsing.Parser;
import io.restassured.response.Response;
import io.restassured.specification.FilterableRequestSpecification;
import io.restassured.specification.FilterableResponseSpecification;
import org.apache.commons.lang3.StringUtils;
import org.apache.commons.lang3.SystemUtils;

import java.io.PrintStream;

public class CustomRequestLoggingFilter implements Filter {

    private static final String HIDDEN = &quot;****&quot;;
    private static final String TAB = &quot;\t&quot;;
    private final PrintStream stream;
    private String path;

    public CustomRequestLoggingFilter(PrintStream stream) {
        this.stream = stream;
    }

    public CustomRequestLoggingFilter() {
        this.stream = System.out;
    }

    public CustomRequestLoggingFilter(String path) {
        this.stream = System.out;
        this.path = path;
    }

    @Override
    public Response filter(FilterableRequestSpecification requestSpec, FilterableResponseSpecification responseSpec, FilterContext ctx) {
        String body = requestSpec.getBody().toString();
        if (this.path != null) {
            body = JsonPath.parse(body).set(path, HIDDEN).jsonString();
            String prettified = new Prettifier().prettify(body, Parser.JSON);
            body = StringUtils.replace(prettified, SystemUtils.LINE_SEPARATOR, SystemUtils.LINE_SEPARATOR + TAB + TAB + TAB + TAB);
        }
        stream.println(body);
        return ctx.next(requestSpec, responseSpec);
    }
}

```

