        getterSetterPair.add(new Pair&lt;&gt;(Fruit::getName, Fruit::setName));

        Fruit fruit1 = null, fruit2 = null;
        for(Pair&lt;Function&lt;Fruit, String&gt;, BiConsumer&lt;Fruit, String&gt;&gt;pair:getterSetterPair) {
