				} else {
					a.href = downloadUrl;
					a.download = filename;
					document.body.appendChild(a);
					a.click();
				}
			} else {
				window.location = downloadUrl;
			}

			setTimeout(function () {
				URL.revokeObjectURL(downloadUrl);
			}, 100); // Cleanup
		}

	})
