        scope.launch(Dispatchers.IO) {
            while (true) {
                val line = stderrChannel.receive()
                if (line.contains(&quot;abc&quot;))
                    throw Exception()
            }
        }
        scope.launch (Dispatchers.IO) {
            process.onExit().get()
            stdoutChannel.cancel()
            stderrChannel.cancel()
        }
        scope.launch (Dispatchers.IO) {
            delay(CONSOLE_MAX_LIFE)
            process.destroy()
