    private fun navigateWithOptions(idToNavigationInto: Int) {
        navController.currentDestination?.let {
            NavOptions.Builder().setPopUpTo(it.id, true).build().also { options -&gt;
                navController.navigate(
                    idToNavigationInto,
                    null,
                    options
                )
            }
        }
    }

