    // Get instantiated and populated somewhere else.
    Map map;
    ArrayList&lt;String&gt; keys;
    int minValue;
    
    void processStreamValue(String key, int value) {
        if (value == minValue) {
            map.put(key, value);
            keys.add(key);
            return;
        }

        if (value &gt; minValue) {
            map.put(key, value);

            foreach (k in keys) { map.remove(k); }
            keys.clear();
            keys.add(key, value);
            minValue = value;
