	private String resolveEnvVars(String input)
	{
		if (null == input)
		{
			return null;
		}
		// match ${ENV_VAR_NAME} or $ENV_VAR_NAME
		Pattern p = Pattern.compile(&quot;\\$\\{(\\w+)\\}|\\$(\\w+)&quot;);
		Matcher m = p.matcher(input); // get a matcher object
