    private static String resolveEnvVars(String input) {
        if (null == input) throw new IllegalArgumentException();
        Pattern p = Pattern.compile(&quot;\\$\\{(\\w+)\\}|\\$(\\w+)&quot;);
        Matcher m = p.matcher(input);

        StringBuilder sb = new StringBuilder();
