public void registerBar(final BeanDefinitionRegistry registry) {
    // Use reflection to get the writeMethod of property the class we want to autowire.
    final Method writeMethod;
    try {
        writeMethod = Bar.class.getMethod(&quot;setFoos&quot;, List.class);
    } catch (final NoSuchMethodException e) {
        throw new Error(e);
    }

    // Create an overriden instance of the MethodParameter to add the desired @Qualifier annotation to it.
    final MethodParameter writeMethodParameter = new MethodParameter(writeMethod, 0) {
        @Override
        public @NonNull Annotation[] getParameterAnnotations() {
            // Call the super method to get the existing annotations on the function, then add the desired
            // @Qualifier annotation to the list.
            Annotation[] annotations = super.getParameterAnnotations();
            annotations                         = Arrays.copyOf(annotations, annotations.length + 1);
            annotations[annotations.length - 1] = new Qualifier() {
                @Override
                public @NonNull String value() {
                    return &quot;A&quot;;
                }

                @Override
                public Class&lt;? extends Annotation&gt; annotationType() {
                    return Qualifier.class;
                }

                @Override
                public String toString() {
                    return &quot;@org.springframework.beans.factory.annotation.Qualifier(\&quot;&quot; + value() + &quot;\&quot;)&quot;;
                }
            };

            // Use reflection to set the private parameterAnnotations field on the super class; this hack to 
            // funtionally declare a @Qualifier annotation does not work if the private field is not set.
            try {
                final Field field = getClass().getSuperclass().getDeclaredField(&quot;parameterAnnotations&quot;);
                field.setAccessible (true);
                field.set           (this, annotations);
            } catch (final NoSuchFieldException | IllegalAccessException e) {
                throw new Error(e);
            }

            return annotations;
        }
    };

    final BeanDefinition beanDefinition = (
        BeanDefinitionBuilder.genericBeanDefinition(Bar.class)
            .addPropertyValue (&quot;id&quot;,  &quot;bar_0001&quot;),
            .addPropertyValue (&quot;...&quot;, &quot;...&quot;)
            .addPropertyValue (&quot;foos&quot;, new DependencyDescriptor(writeMethodParameter, true))
        .getBeanDefinition()
    );

    beanDefinitionRegistry.registerBeanDefinition(&quot;bar&quot;, beanDefinition);
}
