            for(int i=0; i&lt;a.Length; i++){
                subSum+=a[i];
                if(subSum&gt;sumMax){
                    sumMax = subSum;
                    //move high (upper bound) since sumMax increased 
                    iHigh = i;
                    
                    //if the subSum is new, then move lower bound
                    if(subSum == a[i]){
                        iLow = i;
                    }
                }
                subSum = Math.Max(subSum, 0);
