  }

  @Builder(toBuilder = true)
  @Value
  @NoArgsConstructor(force = true, access = AccessLevel.PRIVATE)
  @AllArgsConstructor
  public class SearchResult {
    @Singular
    ImmutableList&lt;String&gt; results;
  }

  // Alternative solution for immutability
  @Builder(toBuilder = true)
  public record SearchResult(
    @Singular
