    public class UserPreference&lt;T&gt; {
        @Id
        @ManyToOne
        @JoinColumn(name = &quot;user_id&quot;)
        @ToString.Exclude
        private UserDetails userDetails;
    
        @Enumerated(EnumType.STRING)
        private PropertyType propertyType;
    
        @Convert(converter = JsonConverter.class)
