{ 
    int n = m - l + 1;
    int b[] = new int[n];

    int i = l;
    int j = m + 1;
    int k = 0;

    while (i &lt;= m) {
        b[k++] = arr[i++]
    }
    
    i = l;
    k = 0;
    
