        }
    }
    while (k &lt; n) {
        arr[i++] = b[k++];
    }
}

void mergeSort2(int arr[], int l, int r, int b[])
{
    if (l &lt; r) {
        int mid = l + (r - l) / 2;
        mergeSort2(arr, l, mid, b);
        mergeSort2(arr, mid + 1, r, b);
        merge2(arr, l, mid, r, b);
    }
}

void mergeSort(int arr[], int l, int r)
{
    if (l &lt; r) {
        int b[] = new int[(r - l) / 2 + 1];
        mergeSort2(arr, l, r, b);
