```java
public class Solution {
    public static void main(String[] args) {
        int[] tab = { 3, 2, 1, 5, 4 };
        int min = Integer.MAX_VALUE; // Initialize min to a large value
        int max = Integer.MIN_VALUE; // Initialize max to a small value
        
        for (int i = 0; i &lt; tab.length; i++) {
            if (tab[i] &lt; min) {
                min = tab[i];
            }
            if (tab[i] &gt; max) {
                max = tab[i];
            }
        }

