	private static void parseRequestHeaders(InputStreamReader reader, HttpRequest httpRequest) 
			throws IOException, HttpParsingException {
		
		HttpHeaders headers = new HttpHeaders();
		StringBuilder headerNameBuffer = new StringBuilder();
		StringBuilder headerValueBuffer = new StringBuilder();
		boolean state = false; // false: parsing name; true: parsing value
		boolean CrlfChecker = false;

		int _byte;
		while ((_byte = reader.read()) &gt;= 0) {
			if (_byte == CR) {
				_byte = reader.read();
				if (_byte == LF) {
					if (headerNameBuffer.length() &gt; 0 &amp;&amp; headerValueBuffer.length() &gt; 0) {
						headers.addHeader(headerNameBuffer.toString().trim(), headerValueBuffer.toString().trim());
						System.out.println(
								&quot;Header added: &quot; + headerNameBuffer.toString() + &quot;: &quot; + headerValueBuffer.toString());
						headerNameBuffer.delete(0, headerNameBuffer.length());
						headerValueBuffer.delete(0, headerValueBuffer.length());
