			// Following the else if below means that it is not yet the end of a header line and there is still chars to be parsed before moving to the next step in the loop.
			else
			if (!state) { // parsing header name
				CrlfChecker = false; // Resetting CrlfChecker since the last char wasn&#39;t LF.
				if (_byte == COLON) {
					state = true; // Switch to Parsing Header Value
				} else {
					headerNameBuffer.append((char) _byte);
				}

			} else { // parsing header value
				CrlfChecker = false; // Resetting CrlfChecker since the last char wasn&#39;t LF.
				headerValueBuffer.append((char) _byte);
